CREATE TABLE `article` (
  `a_id` int(20) NOT NULL AUTO_INCREMENT,
  `article_title` varchar(30) NOT NULL,
  `article_content` varchar(10000) NOT NULL,
  `article_class` varchar(10) NOT NULL,
  `article_time` varchar(20) NOT NULL,
