  PRIMARY KEY (`bg_id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;

/*Data for the table `bgimg` */

insert  into `bgimg`(`bg_id`,`bg_url`) values (1,'img/bg/bg01.jpg');

/*Table structure for table `blog_img` */

DROP TABLE IF EXISTS `blog_img`;

CREATE TABLE `blog_img` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `u_class` int(2) NOT NULL,
  `u_path` varchar(55) NOT NULL,
